mapper.select:
SELECT m.id, u.id AS user_id, u.nick_name AS user_nick_name, u.avatar AS user_avatar, m.content_type, m.text, m.medias, m.location, m.like_counts, m.comment_counts, m.create_time FROM moment m JOIN users u ON u.id = m.publish_user_id WHERE ( m.publish_user_id = #{userId} OR EXISTS ( SELECT 1 FROM friend f WHERE f.user_id = #{userId} AND f.friend_id = m.publish_user_id ) )
AND m.id < #{lastId}
ORDER BY m.id DESC LIMIT #{pageSize}

SELECT m.id, u.id AS user_id, u.nick_name AS user_nick_name, u.avatar AS user_avatar, m.content_type, m.text, m.medias, m.location, m.like_counts, m.comment_counts, m.create_time FROM moment m JOIN users u ON u.id = m.publish_user_id WHERE m.publish_user_id = #{userId}
AND m.id < #{lastId}
ORDER BY m.id DESC LIMIT #{pageSize}
